mapper.select:
SELECT real_name, diet_date, meal_type, food_name, quantity, image_path FROM diet_food_view
user_id = #{userId}
AND diet_date = #{dietDate}
AND meal_type = #{mealType}
ORDER BY diet_date DESC

SELECT DISTINCT diet_date, meal_type
FROM diet_food_view
WHERE user_id = #{userId}

    AND diet_date = #{dietDate}


    AND meal_type = #{mealType}

ORDER BY diet_date DESC, FIELD(meal_type, '加餐', '晚餐', '午餐', '早餐')

select * from diet_exercise_diary

    user_id=#{userId}

        and diary_date=#{diaryDate}


order by diary_date desc

SELECT dfv.real_name, dfv.diet_date, dfv.meal_type, fnv.nutrient_name, ROUND(SUM(dfv.quantity / 100.0 * fnv.amount), 2) AS intake_amount FROM diet_food_view dfv JOIN food_nutrient_view fnv ON dfv.food_name = fnv.food_name
dfv.user_id = #{userId}
AND dfv.diet_date = #{dietDate}
AND dfv.meal_type = #{mealType}
GROUP BY dfv.real_name, dfv.diet_date, dfv.meal_type, fnv.nutrient_name
